import { useContext, useEffect, useState } from 'react';
import '../App.css';
import { userDataContext } from '../context/usercontext';
import { PlayPause, Circle, Prev, Next } from '../utils/musics_functions';
import prev from '../images/previous.png';
import next from '../images/fast-forward.png';
import pause from '../images/play.png';
import circle from '../images/replay.png';
import logolist from '../images/playlist.png';
import logo from '../images/DQBBAtuUEAIcZuL.jpg';

function Mainbody() {
    const { userData, setUserData } = useContext(userDataContext);

    // useEffect(() => {
    //     setUserData({userenter: false});
    // }, [])
    // const { useruid, databaseref } = userData;

    // setUserData({ useruid: 'hjdg' })
    // console.log(databaseref)

    // const [auth, setAuth] = useState(null);
    // const [database, setDatabase] = useState(null);

    // useEffect(() => {
    //     // Initialize Firebase
    //     firebase.initializeApp(firebaseConfig);

    //     setAuth(firebase.auth());
    //     setDatabase(firebase.database())
    // }, []);

    const [Artist, setArtist] = useState(null);
    const [Musicname, setMusicname] = useState(null);
    const [Musicsource, setMusicsorce] = useState(null);


    const handlePlayPause = () => {
        PlayPause();
    }

    const handlePrev = () => {
        Prev()
    }

    const handleNext= () => {
        Next()
    }

    const handleCircle= () => {
        Circle()
    }

    return (
        <>
            <div className="top-bar">
                <i id="more-music" className="material-icons"><img src={logolist} className="im" /></i>
                <div className="music-list">
                    <div className="header">
                        <div className="row">
                            <i className="list material-icons"><img src={logolist} className="im" /></i>
                            <span>Music list</span>
                        </div>
                        <i id="close" className="material-icons">close</i>
                    </div>
                    <ul>
                    </ul>
                    <audio id="main-audio" src={Musicsource || "./src/songs/music1.mp3"}></audio>
                </div>


                <span>Now Playing</span>
                <button type="button" id="likedislike" className="listc">Like</button>
                <button type="button" id="changelist" className="listc">Choose user list</button>
            </div>
            <div className="img-area">
                <img src={logo} alt="" />
            </div>
            <div className="song-details">
            {/* <p className="name" id="nameuser">{userName || 'Current user'}</p> */}
                <p className="name">{Musicname || "Error"}</p>
                <p className="artist">{Artist || "Error"}</p>
            </div>
            <div className="controlss">
                <div className='controls'>
                    <button type="button" id="prev" className="material-icons prev musiccontrlole" onClick={handlePrev}><img src={prev} className="im" /></button>
                </div>
                <div className='controls'>
                    <button type="button" id="puase" className="material-icons play musiccontrlole" onClick={handlePlayPause}><img src={pause} className="im" /></button>
                </div>
                <div className='controls'>
                    <button type="button" id="next" className="material-icons next musiccontrlole" onClick={handleNext}><img src={next} className="im" /></button>
                </div>
                <div className='controls'>
                    <button type="button" id="repeat-plist" className="material-icons repeat musiccontrlole" title="Playlist looped" onClick={handleCircle}><img src={circle} className="im" /></button>
                </div>
            </div>



        </>
    );
}

export default Mainbody;
