import { useState } from 'react';
import './App.css';
import logo from '../src/images/DQBBAtuUEAIcZuL.jpg';


import prev from '../src/images/previous.png';
import next from '../src/images/fast-forward.png';
import pause from '../src/images/play.png';
import circle from '../src/images/replay.png';

// import logo from './logo.svg';
import Loginheader from './components/loginheader';
import { userDataProvider } from './context/usercontext';

function App() {
  const [userData, setUserData] = useState({
    useruid: null, databaseref: null, userenter: false,
  });


  return (
    <userDataProvider value={{ userData, setUserData: (payload) => setUserData((prevState) => ({ ...prevState, ...payload })) }} >
      <div className='body'>
        <div className="wrapper">

          <Loginheader></Loginheader>


          <div className="top-bar">
            <i id="more-music" className="material-icons">queue_music</i>
            <div className="music-list">
              <div className="header">
                <div className="row">
                  <i className="list material-icons">queue_music</i>
                  <span>Music list</span>
                </div>
                <i id="close" className="material-icons">close</i>
              </div>
              <ul>
              </ul>
              <audio id="main-audio" src=""></audio>
            </div>


            <span>Now Playing</span>
            <button type="button" id="likedislike" className="listc">Like</button>
            <button type="button" id="changelist" className="listc">Choose user list</button>
          </div>
          <div className="img-area">
            <img src={logo} alt="" />
          </div>
          <div className="song-details">
            <p className="name">t</p>
            <p className="artist">t</p>
          </div>
          <div className="controls">
            {/* <>
              <button type="button" id="prev" className="material-icons prev musiccontrlole"><img src={prev} className="im" /></button>
              &nbsp;
              <button type="button" id="puase" className="material-icons play musiccontrlole"><img src={pause} className="im" /></button>
              &nbsp;
              <button type="button" id="next" className="material-icons next musiccontrlole"><img src={next} className="im" /></button>
              &nbsp;
              <button type="button" id="repeat-plist" className="material-icons repeat musiccontrlole" title="Playlist looped"><img src={circle} className="im" /></button>
            </> */}
            <div>
              <button type="button" id="prev" className="material-icons prev musiccontrlole"><img src={prev} className="im" /></button>
            </div>
            <div>
              <button type="button" id="puase" className="material-icons play musiccontrlole"><img src={pause} className="im" /></button>
            </div>
            <div>
              <button type="button" id="next" className="material-icons next musiccontrlole"><img src={next} className="im" /></button>
            </div>
            <div>
              <button type="button" id="repeat-plist" className="material-icons repeat musiccontrlole" title="Playlist looped"><img src={circle} className="im" /></button>
            </div>
          </div>




        </div>
      </div>
    </userDataProvider>
  );
}

export default App;
