import React, { useState, useEffect } from "react";

const useAudio = startUrl => {
    const [url, setUrl] = useState(startUrl)
    const [audio, setAudio] = useState(new Audio(startUrl));

    const [playing, setPlaying] = useState(false);
    const [_playAfterSet, playAfterSet] = useState(false);

    const [songsend, setsongend] =useState(false);

    const toggle = () => setPlaying(!playing);

    useEffect(() => {
        setAudio(new Audio(url));
        if (_playAfterSet) {
            playAfterSet(false);
            toggle()
        }
    }, [url])

    useEffect(() => {
        console.log('playing', playing, 'url', url)
        playing ? audio.play() : audio.pause();
    },
        [playing]
    );

    useEffect(() => {
        audio.addEventListener('ended', () => {
            console.log('ended')
            setPlaying(false);
            setsongend(true);
        });
        return () => {
            audio.removeEventListener('ended', () => setPlaying(false));
        };
    }, [audio]);

    return [playing, toggle, setUrl, playAfterSet];
};

export default useAudio;
