const Circle = async ()=>{
    circle = !circle;
};



function prevMusic()
{
    musicIndex--;
    if (musicIndex < 1)
    {
        musicIndex=allMusic.length;
    }

    for (let i=0; i<lists.length; i++)
    {
        if (lists[i]==musicIndex)
        {
            loadMusic(musicIndex);
            playMusic();
            return;
        }
    }
    console.log(lists.length, "listlength")
    if (lists.length == 0)
    {
        loadMusic(musicIndex);
        playMusic();
        return;
    }
    prevMusic()
}
function nextMusic()
{
    musicIndex++;
    if (musicIndex > allMusic.length)
    {
        musicIndex=1;
    }
    for (let i=0; i<lists.length; i++)
    {
        if (lists[i]==musicIndex)
        {
            loadMusic(musicIndex);
            playMusic();
            return;
        }
    }
    console.log(lists.length, "listlength")
    if (lists.length == 0)
    {
        loadMusic(musicIndex);
        playMusic();
        return;
    }
    nextMusic()
}




const Prev = async ()=>{
    prevMusic();
  };

const Next = async ()=>{
    nextMusic();
  };



export {
    PlayPause,
    Circle,
    Prev,
    Next
  };
