import { useContext, useEffect, useState } from 'react';
import '../App.css';
import { userDataContext } from '../context/usercontext';

function Loginheader() {
    const { userData, setUserData } = useContext(userDataContext);
    // const { useruid, databaseref } = userData;

    // setUserData({ useruid: 'hjdg' })
    // console.log(databaseref)

    const [auth, setAuth] = useState(null);
    const [database, setDatabase] = useState(null);

    useEffect(() => {
        // Initialize Firebase
        firebase.initializeApp(firebaseConfig);

        setAuth(firebase.auth());
        setDatabase(firebase.database())
    }, []);

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [userName, setUserName] = useState(null);


    const handleLogin = () => {
        auth.signInWithEmailAndPassword(email, password)
            .then(function () {
                var user = auth.currentUser
                nickname.innerText = email;
                var database_ref = database.ref()
                var user_data = {
                    last_login: Date.now()
                }
                database_ref.child('users/' + user.uid).update(user_data)
                setUserData({ databaseref: database_ref, useruid: user.uid })
                setUserName(email)
                alert('User Logged In!!')
            })
        userenter = true;
    }

    const handleRegister = () => {
        auth.createUserWithEmailAndPassword(email, password)
            .then(function () {
                var user = auth.currentUser
                var database_ref = database.ref()

                var user_data = {
                    email: email,
                    list: objectt,
                    last_login: Date.now()
                }
                database_ref.child('users/' + user.uid).set(user_data)
                //   console.log("catch")

                alert('User Created!!')
            })
    }

    return (
        <>
            <div>
                <input type="email" id="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
                <input type="password" id="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            </div>



            <div className="admbutons">
                <button type="button" id="login" onClick={handleLogin}>Login</button>
                <button type="button" id="register" onClick={handleRegister}>Register</button>
                <p className="name" id="nameuser">{userName || 'Not logged in'}</p>
            </div>
        </>
    );
}

export default Loginheader;
