import { initializeApp } from "firebase/app";
import {
  GoogleAuthProvider,
  getAuth,
  signInWithPopup,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  sendPasswordResetEmail,
  signOut,
} from "firebase/auth";
import something from 'firebase/database'
import {
  getFirestore,
  query,
  getDocs,
  collection,
  where,
  addDoc,
} from "firebase/firestore";
const firebaseConfig = {
    apiKey: "AIzaSyCKFsCChb9mlTV1v-NT_8FUSysj9kH3mtk",
    authDomain: "autorization-350818.firebaseapp.com",
    projectId: "autorization-350818",
    storageBucket: "autorization-350818.appspot.com",
    messagingSenderId: "490521543967",
    appId: "1:490521543967:web:df9c8a794de6a1a1275c2a",
    measurementId: "G-BEC80WQCVS",
    databaseURL: "https://autorization-350818-default-rtdb.europe-west1.firebasedatabase.app/"
};
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = firebase.database();
const googleProvider = new GoogleAuthProvider();

const logInWithEmailAndPassword = async (email, password, setUserData) => {
  try {
    const res = await signInWithEmailAndPassword(auth, email, password);
    var user_data = {
                        last_login: Date.now()
                    }
                    something()
    db.ref('users/' + res.user.uid).update(user_data)
    // Firebase  .child('users/' + user.uid).update(user_data)
    setUserData({useruid: res.user.uid, userenter: true})
    alert('User Logged In!!')
    return true;
  } catch (err) {
    console.error(err);
    alert(err.message);
    return false;
  }
};
const registerWithEmailAndPassword = async (email, password) => {
  try {
    const res = await createUserWithEmailAndPassword(auth, email, password);
    const user = res.user;
    await addDoc(collection(db, "users"), {
      uid: user.uid,
      name: email,
      authProvider: "local",
      email,
    });
    alert('User Created!!')
    return true;
  } catch (err) {
    console.error(err);
    alert(err.message);
    return false;
  }
};

export {
  auth,
  db,
  logInWithEmailAndPassword,
  registerWithEmailAndPassword,
};
